INSERT INTO book (title, price, pages, isbn, nuart, note, summary, hook, marketing, cover_filename, release_date)
VALUES ('One Piece Tome 1', 13.0, 192, '978-2-35348-278-8', 'xxx xxx x', 'note',
        'One Piece raconte les aventures d''une bande de pirates, menée par leur capitaine Monkey D. Luffy (qui a pour ambition de devenir le roi des pirates) et lancée à la recherche du trésor, nommé One Piece, du légendaire roi des pirates Gol D. Roger, mort sans avoir révélé l''emplacement de son butin.',
        NULL, NULL,
        '040982c3-c83b-4249-aa82-f5490ba8143f', '2025-11-15'),
       ('One Piece Tome 2', 13.0, 192, '978-2-35348-279-5', 'xxx xxx x', 'note',
        'One Piece raconte les aventures d''une bande de pirates, menée par leur capitaine Monkey D. Luffy (qui a pour ambition de devenir le roi des pirates) et lancée à la recherche du trésor, nommé One Piece, du légendaire roi des pirates Gol D. Roger, mort sans avoir révélé l''emplacement de son butin.',
